mapper.select:
	SELECT 
      ID , 
      DESCRIPCION , 
      STOCK , 
      ID_BOLETA 
	FROM
	ITEM
	WHERE 1 = 1

		AND ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND STOCK = #{stock, jdbcType=INTEGER}


		AND ID_BOLETA = #{idBole.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO ITEM( ID , DESCRIPCION , STOCK , ID_BOLETA ) (
SELECT #{element.id} , #{element.descripcion} , #{element.stock} , #{element.idBole.id} from dual
)

mapper.delete:
    DELETE FROM ITEM
	WHERE 1 = 1

		AND ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND STOCK = #{stock, jdbcType=INTEGER}


		AND ID_BOLETA = #{idBole.id}
